import { Component } from '@angular/core';
import { TableColumn } from 'src/@vex/interfaces/table-column.interface';

@Component({
  selector: 'vex-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.scss']
})
export class MenuComponent {

  entity = 'menus';
  columns: TableColumn<any>[] = [
    { label: 'Carta', property: 'title', type: 'text', visible: true, cssClasses: ['font-medium'] },
    { label: 'Activa', property: 'active', type: 'active', visible: true },
    { label: 'Actions', property: 'actions', type: 'button', visible: true }
  ];
}
<vex-page-layout>

  <vex-secondary-toolbar>
    <vex-breadcrumbs [crumbs]="['Menus', 'Lista']" class="flex-auto"></vex-breadcrumbs>

    <button class="ml-2" color="primary" mat-icon-button type="button">
      <mat-icon svgIcon="mat:more_vert"></mat-icon>
    </button>
  </vex-secondary-toolbar>
  <vex-page-layout-content class="-mt-6 fullwidth">
      <vex-aio-table [columns]="columns" [entity]="entity" [title]="'Menús'" [route]="'menu'"></vex-aio-table>
  </vex-page-layout-content>
</vex-page-layout>
