import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators, AbstractControl } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { fadeInUp400ms } from 'src/@vex/animations/fade-in-up.animation';
import { stagger20ms } from 'src/@vex/animations/stagger.animation';
import { LangService } from 'src/service/langService';

@Component({
  selector: 'vex-language-create-update',
  templateUrl: './language-create-update.component.html',
  styleUrls: ['./language-create-update.component.scss'],
  animations:[stagger20ms, fadeInUp400ms]
})
export class LanguageCreateUpdateComponent {
  langForm: FormGroup;
  langId: string | null = null;
  mode: string | null = null;
  defaults: any;

  constructor(
    private fb: FormBuilder,
    private langService: LangService,
    private route: ActivatedRoute,
    private router: Router
  ) {
    this.langForm = this.fb.group({
      title: ['', Validators.required],
      code: ['', [Validators.required, this.lengthValidator]]
    });
  }

  async ngOnInit() {
    // if you are editing an existing lang, set langId
    // and load the lang details using getLangById()
    this.langId = this.route.snapshot.paramMap.get('id');
    if (this.langId !== null) {
      this.mode = 'update';
      const langData = await this.langService.getLangById(this.langId);
      if (langData) {
        this.langForm.patchValue({
          name: langData.name,
          code: langData.code
        });
      }
    } else {
      this.defaults = {} as any;

    }
  }

  save() {
    if (this.langId) {
      this.langService.updateLang(this.langId, this.langForm.value);
    } else {
      this.langService.createLang(this.langForm.value);
    }
  }

  // Custom Validator
  lengthValidator(control: AbstractControl) {
    const value = control.value;
    const regex = /^[a-zA-Z]{2,5}$/;
    return regex.test(value) ? null : { length: true };
  }

  cancel() {
    this.router.navigate(['/language']); // Navigating to /language on cancel
  }
}
<vex-secondary-toolbar current="Idiomas">
  <vex-breadcrumbs [crumbs]="['Idiomas', 'Gestión de Idiomas']" class="flex-auto"></vex-breadcrumbs>

  <button class="ml-2" color="primary" mat-icon-button type="button">
    <mat-icon svgIcon="mat:more_vert"></mat-icon>
  </button>
</vex-secondary-toolbar>

<div [@stagger]="true" class="p-gutter container">

  <div class="flex flex-col sm:flex-row gap-4">
    <div @fadeInUp class="card flex-auto">
      <div class="px-6 py-4 border-b flex items-center">
        <h2 class="title m-0">Gestor de idiomas</h2>
      </div>

      <div class="px-6 py-4 flex flex-col" [formGroup]="langForm">
        <mat-form-field>
          <mat-label>Nombre</mat-label>
          <input matInput formControlName="title" required>
          <mat-hint class="mat-small" *ngIf="langForm.get('title').invalid && langForm.get('title').touched">
            Este campo es obligatorio
          </mat-hint>
        </mat-form-field>

        <mat-form-field>
          <mat-label>Código de dos letras</mat-label>
          <input matInput formControlName="code" required>
          <mat-hint class="mat-small" *ngIf="langForm.get('code').invalid && langForm.get('code').touched">
            Este campo es obligatorio
          </mat-hint>
          <mat-hint *ngIf="langForm.controls.code.invalid && langForm.controls.code.touched">
            Debe ser de 2 a 5 letras
          </mat-hint>
        </mat-form-field>

        <div class="flex items-center justify-end gap-2">
          <button mat-button type="button" (click)="cancel()">Cancel</button> <!-- Added (click) event here -->
          <button color="primary" mat-raised-button type="button" (click)="save()">Guardar</button>
        </div>
      </div>
    </div>
  </div>
</div>
