import { Component, OnInit } from '@angular/core';
import { UntypedFormBuilder, UntypedFormGroup } from '@angular/forms';
import { fadeInUp400ms } from 'src/@vex/animations/fade-in-up.animation';
import { stagger20ms } from 'src/@vex/animations/stagger.animation';
import { getFirestore, Firestore, addDoc, collection, updateDoc, doc, deleteDoc, getDocs, getDoc, writeBatch  } from '@angular/fire/firestore';
import { getStorage, ref, deleteObject, uploadString, getDownloadURL, uploadBytesResumable } from "firebase/storage";
import { MatSnackBar } from '@angular/material/snack-bar';
import { ActivatedRoute, Router } from '@angular/router';


@Component({
  selector: 'vex-menu-create-update',
  templateUrl: './menu-create-update.component.html',
  styleUrls: ['./menu-create-update.component.scss'],
  animations: [fadeInUp400ms, stagger20ms]
})
export class MenuCreateUpdateComponent implements OnInit {

  static id = null;
  docId: any = null;;
  type: any = null;

  form: UntypedFormGroup;
  mode: 'create' | 'update' = 'create';
  defaults: any;
  options: any = [
    {
      title: 'Restaurante',
      type: 'comida'
    },
    {
      title: 'Vinos',
      type: 'vinos'
    },
    {
      title: 'Cócteles',
      type: 'cocteles'
    }
  ];

  snacks: any = [];
  starters: any = [];
  fishes: any = [];
  meats: any = [];
  deserves: any = [];
  coffes: any = [];
  drinks: any = [];
  champagnes: any = [];
  whiteWine: any = [];
  roseWine: any = [];
  redWine: any = [];
  sweetWine: any = [];
  langs: any[] = [];
  menuTitle: string = null;
  isActive: boolean = false;
  loading: boolean = true;

  tabs: any = [];
  selectedTabIndex = 0;

  constructor(private fb: UntypedFormBuilder, private firestore: Firestore, private matSnackBar: MatSnackBar, private router: Router, private route: ActivatedRoute) {
  }

  ngOnInit() {
    // Obtén el ID de la ruta.
    this.docId = this.route.snapshot.paramMap.get('id');
    //this.getOptions();
    this.getLanguages();
    if (this.docId !== null) {
      this.mode = 'update';
      this.type = 'comida';
      this.getById();
    } else {
      this.defaults = {} as any;

    }
  }


  async getLanguages() {
    const db = getFirestore();
    const languagesCollection = collection(db, 'languages');
    const languagesSnapshot = await getDocs(languagesCollection);
    this.langs = languagesSnapshot.docs.map(doc => doc.data().code);
    if (this.mode === 'create') {
      this.loadCreateData();

    }
  }


  async getOptions() {
    const db = getFirestore();
    const optionsCollection = collection(db, 'options');
    const optionsSnapshot = await getDocs(optionsCollection);
    this.options = optionsSnapshot.docs.map(doc => doc.data());
    this.getLanguages();

  }

  handleSelection(value: string) {
    this.type = value;
    console.log(`Tipo seleccionado: ${this.type}`);
  }


  isCreateMode() {
    return this.mode === 'create';
  }

  isUpdateMode() {
    return this.mode === 'update';
  }

  addSnack() {
    this.langs.forEach(element => {
      this.snacks.langs[element].push({
        title: '',
        price: '',
        description: ''
      });
    });
  }

