      );
    }
  }

  async deleteChampagne(lang: string, i: number) {
    // Create a reference to the file to delete
    const storage = getStorage();
    const storageRef = ref(storage, this.champagnes.langs[lang][i].imageRead);

    // Delete the file
    try {
      await deleteObject(storageRef);
      console.log('File deleted');
    } catch(error) {
      console.error('Error deleting file', error);
    }
  }

  async deleteWhiteWine(lang: string, i: number) {
    // Create a reference to the file to delete
    const storage = getStorage();
