  removeSnack(index: number) {

    for (const lang of this.langs) {
      this.snacks.langs[lang].splice(index, 1);
    }
  }

  removeStarter(index: number) {

    for (const lang of this.langs) {
      this.starters.langs[lang].splice(index, 1);
    }
  }

  removeMeat(index: number) {

    for (const lang of this.langs) {
      this.meats.langs[lang].splice(index, 1);
    }
  }

  removeFish(index: number) {

    for (const lang of this.langs) {
      this.fishes.langs[lang].splice(index, 1);
    }
  }

  removeDesserve(index: number) {

