      this.coffes.langs[lang].splice(index, 1);
    }
  }

  removeDrink(index: number) {
    for (const lang of this.langs) {
      this.drinks.langs[lang].splice(index, 1);
    }
  }

  removeChampagne(index: number, lang: string) {
    this.deleteChampagne(lang, index);

    for (const lang of this.langs) {
      this.champagnes.langs[lang].splice(index, 1);
    }
  }

  removeWhiteWine(index: number, lang: string) {
    this.deleteWhiteWine(lang, index);

    for (const lang of this.langs) {
      this.whiteWine.langs[lang].splice(index, 1);
    }
  }

  removeRoseWine(index: number, lang: string) {
    this.deleteRoseWine(lang, index);

    for (const lang of this.langs) {
      this.roseWine.langs[lang].splice(index, 1);
    }
  }

  removeRedWine(index: number, lang: string) {
    this.deleteRedWine(lang, index);

    for (const lang of this.langs) {
      this.redWine.langs[lang].splice(index, 1);
    }
  }

  removeSweetWine(index: number, lang: string) {
    this.deleteSweetWine(lang, index);

    for (const lang of this.langs) {
      this.sweetWine.langs[lang].splice(index, 1);
    }
  }

  loadCreateData() {
    this.snacks.langs = {};
    this.starters.langs = {};
    this.fishes.langs = {};
    this.meats.langs = {};
    this.deserves.langs = {};
    this.coffes.langs = {};
    this.drinks.langs = {};
    this.champagnes.langs = {};
    this.whiteWine.langs = {};
    this.roseWine.langs = {};
    this.redWine.langs = {};
    this.sweetWine.langs = {};

    this.langs.forEach(element => {
      this.snacks.langs[element] = [];
      this.starters.langs[element] = [];
      this.fishes.langs[element] = [];
      this.meats.langs[element] = [];
      this.deserves.langs[element] = [];
      this.coffes.langs[element] = [];
      this.drinks.langs[element] = [];
      this.champagnes.langs[element] = [];
      this.whiteWine.langs[element] = [];
      this.roseWine.langs[element] = [];
      this.redWine.langs[element] = [];
      this.sweetWine.langs[element] = [];
    });
    this.loading = false;
  }


  submit() {
    if (this.mode === 'update') {
      this.updateMenu();
    } else {
      this.createMenu();
    }
  }

  async createMenu() {
    try {
      if (this.isActive) {
        const menuRef = collection(this.firestore, 'menus');
        const menuSnap = await getDocs(menuRef);
        const batch = writeBatch(this.firestore);

        menuSnap.forEach((doc) => {
          batch.update(doc.ref, { active: false });
        });

        await batch.commit();
      }
      const docRef = await addDoc(collection(this.firestore, 'menus'), this.tabs);
      this.matSnackBar.open("El menu se ha creado correctamente", 'CERRAR', {duration: 3000});
      this.router.navigate(['menu']);
    } catch (e) {
      console.error('Error adding document: ', e);
    }
  }

  async updateMenu(): Promise<void> {
    try {

      if (this.isActive) {
        const menuRef = collection(this.firestore, 'menus');
        const menuSnap = await getDocs(menuRef);
        const batch = writeBatch(this.firestore);

        menuSnap.forEach((doc) => {
          batch.update(doc.ref, { active: false });
        });

        await batch.commit();
      }

      const menuRef = doc(collection(this.firestore, 'menus'), this.docId);
      await updateDoc(menuRef, this.tabs);
      this.matSnackBar.open("El menu se ha modificado correctamente", 'CERRAR', {duration: 3000});
      this.router.navigate(['menu']);    } catch (error) {
      console.error('Error updating menu:', error);
    }
  }

  async getById() {


    if (this.docId) {
      const db = getFirestore();
      const docRef = doc(db, "menus", this.docId);

      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        // El documento existe.
        this.tabs = docSnap.data();
        this.loading = false;
      } else {
        // El documento no existe.
        throw new Error("No document with given id");
      }
    }
  }

  setTitle(event, tab) {
    tab.title = event.value;
  }

  addTab() {

    this.tabs.push({ title: '', subTabs:[], type: this.type.type });
    this.selectedTabIndex = this.tabs.length - 1;  // Cambia al índice de la nueva pestaña
    this.addSubTab();
  }

  getTabsByType() {
    const tabs = this.tabs.filter(t => t.type === this.type.type);
    return tabs.length > 0 ? tabs : [];
  }

  addSubTab() {

    this.tabs[this.selectedTabIndex].subTabs.push({ langs: {} });

    this.langs.forEach(element => {

      this.tabs[this.selectedTabIndex].subTabs.forEach(subT => {
        subT.langs[element] = [];/*
          subT.langs[element].push({
            image: null,
            title: '',
            description: '',
            bottlePrice: '',
            cupPrice: '',
            imageRead: '',
            origin: ''
          });*/
      });
    });
  }

  removeTab(index: number) {
    this.tabs.splice(index, 1);
    if (this.selectedTabIndex >= index) {
      this.selectedTabIndex = Math.max(this.selectedTabIndex - 1, 0);
    }
  }

  removeSubTab(index: number, tab) {
    tab.subTabs.splice(index, 1);
  }

  addToTab(tab: any, subTab: any) {
    this.langs.forEach(element => {

      if (tab.type === 'vinos') {
        subTab.langs[element].push({
          image: null,
          title: '',
          description: '',
          bottlePrice: '',
          cupPrice: '',
          imageRead: '',
          origin: ''
        })
      } else {

        subTab.langs[element].push({
          title: '',
          price: '',
          description: ''
        });
      }
    });
  }

  removeToTab(lang: string, tab: any, index: number) {

    this.deleteImage(lang, index, tab);
    for (const lang of this.langs) {
      tab.langs[lang].splice(index, 1);
    }
  }

  onSelect(event, lang: string, i: number, tab: any) {
    if (event.addedFiles.length > 0) {
      const file = event.addedFiles[0];
      if (!(file instanceof File)) {
        console.error('El archivo seleccionado no es un objeto de tipo File:', file);
        return;
      }
      const storage = getStorage();

      // Creamos un identificador único para el nombre de cada imagen.
      const uniqueId = Math.random().toString(36).substring(2);

      // Creamos una referencia al lugar donde queremos subir el archivo: 'images/{uniqueId}'
      const storageRef = ref(storage, `images/${uniqueId}`);

      // Subimos el archivo a Firebase Storage.
      const uploadTask = uploadBytesResumable(storageRef, file);

      // Escuchamos los cambios de estado de la subida del archivo y capturamos los datos del archivo subido.
      uploadTask.on('state_changed',
        (snapshot) => {},
        (error) => {
          console.error('Error al subir el archivo:', error);
        },
        () => {
          getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
            console.log('File available at', downloadURL);
            tab.langs[lang][i].imageRead = downloadURL;

            for (const lang of this.langs) {
              tab.langs[lang][i].imageRead = downloadURL;
            }
          });
        }
      );
    }
  }

  async deleteImage(lang: string, i: number, tab: any) {
    // Create a reference to the file to delete
    const storage = getStorage();
    const storageRef = ref(storage, tab.langs[lang][i].imageRead);

    // Delete the file
    try {
      await deleteObject(storageRef);
      console.log('File deleted');
    } catch(error) {
      console.error('Error deleting file', error);
    }
  }
}

<vex-secondary-toolbar current="Menu">
  <vex-breadcrumbs [crumbs]="['Cartas', 'Gestión de Cartas']" class="flex-auto"></vex-breadcrumbs>

  <button class="ml-2" color="primary" mat-icon-button type="button">
    <mat-icon svgIcon="mat:more_vert"></mat-icon>
  </button>
