<div [@stagger]="true" class="p-gutter container" style="max-width: none" *ngIf="!loading">

  <div style="display: flex; align-items: center; justify-content: space-between;">
    <mat-form-field appearance="fill" style="width: 50%">
      <mat-label>Que tipo de carta quieres crear</mat-label>
      <mat-select (selectionChange)="handleSelection($event.value)" [(ngModel)]="type">
        <mat-option *ngFor="let item of options" [value]="item">{{item.title}}</mat-option>
      </mat-select>
    </mat-form-field>
    <button mat-raised-button color="primary" style="float:right" *ngIf="type !== null" (click)="submit()">Guardar</button>
  </div>

 <!-- Contenedor Flexbox para alinear los elementos horizontalmente -->
 <div style="display: flex; gap: 20px; align-items: center; justify-content: start;">
  <!-- Input para el título -->
  <mat-form-field appearance="fill" style="flex: 1;">
    <mat-label>Título para la carta</mat-label>
    <input matInput placeholder="Ingrese el título de la carta" [(ngModel)]="menuTitle">
  </mat-form-field>

  <!-- Toggle para el atributo 'active' -->
  <mat-slide-toggle style="flex: 1;" [(ngModel)]="isActive">¿Activar?</mat-slide-toggle>

</div>
<button *ngIf="type!==null" mat-raised-button color="primary" (click)="addTab()" style="float: left;">Añadir</button>
<button *ngIf="tabs.length > 0" mat-raised-button color="primary" (click)="addSubTab()" style="float: left;">Añadir sub apartado</button>

  <mat-tab-group style="width:100%">

    <mat-tab *ngFor="let tab of getTabsByType(); let idx = index">
      <ng-template mat-tab-label>
        <input [(ngModel)]="tab.title" placeholder="Título de la pestaña" (input)="setTitle($event, tab)">
        <button mat-button (click)="removeTab(idx)">X</button>
      </ng-template>
      <!-- Aquí va el contenido de tu pestaña, como los elementos del menú -->

      <mat-tab-group *ngIf="type.type === 'vinos'">
        <mat-tab *ngFor="let subTab of tab.subTabs; let subI = index">
          <ng-template mat-tab-label>
            <input [(ngModel)]="subTab.title" placeholder="Título de la pestaña" (input)="setTitle($event, tab)">
            <button mat-button (click)="removeSubTab(subI, subTab)">X</button>
          </ng-template>
          <mat-tab-group>
            <mat-tab *ngFor="let lang of langs; let i = index" label="{{lang.toUpperCase()}}" >

              <button class="rounded-full" color="primary" mat-raised-button type="button" (click)="addToTab(tab, subTab)" style="float:right">
                <mat-icon class="ltr:-ml-1 rtl:-mr-1 ltr:mr-2 rtl:ml-2"
                          svgIcon="mat:add"></mat-icon>
                <span>Añadir</span>
              </button>

              <div *ngFor="let item of subTab.langs[lang]; let i = index" style="float:left; margin: 20px 10px 0 0; width:28%;">
                <button mat-icon-button color="warn" (click)="removeToTab(lang, subTab, i)">
                  <mat-icon svgIcon="mat:delete"></mat-icon>
                </button>

                <div class="dropzone">
                  <ngx-dropzone [multiple]="false" (change)="onSelect($event, lang, i, subTab)">
                    <img *ngIf="subTab.langs[lang][i]?.imageRead !== null" [src]="subTab.langs[lang][i]?.imageRead" alt="Imagen">
                  </ngx-dropzone>
                </div>
                <mat-form-field class="example-full-width">
                  <mat-label>Titulo</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].title" placeholder="Nombre">
                </mat-form-field>

                <mat-form-field class="example-full-width">
                  <mat-label>Precio</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].price" placeholder="Precio">
                </mat-form-field>

