import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators, AbstractControl } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { fadeInUp400ms } from 'src/@vex/animations/fade-in-up.animation';
import { stagger20ms } from 'src/@vex/animations/stagger.animation';
import { CrudService } from 'src/service/crudService';

@Component({
  selector: 'vex-options-create-update',
  templateUrl: './options-create-update.component.html',
  styleUrls: ['./options-create-update.component.scss'],
  animations:[stagger20ms, fadeInUp400ms]
})
export class OptionsCreateUpdateComponent {
  form: FormGroup;
  langId: string | null = null;
  mode: string | null = null;
  defaults: any;
  thumbnail: boolean = false;
  drink: boolean = false;
  active: boolean = false;

  constructor(
    private fb: FormBuilder,
    private crudService: CrudService,
    private route: ActivatedRoute,
    private router: Router
  ) {
    this.form = this.fb.group({
      title: ['', Validators.required],
      title_en: ['', Validators.required],
      title_it: ['', Validators.required],
      title_de: ['', Validators.required],
      thumbnail: [this.thumbnail, Validators.required],
      drink: [this.drink, Validators.required],
      active: [this.active, Validators.required],
      type: ['', Validators.required]
    });
  }

  async ngOnInit() {
    // if you are editing an existing lang, set langId
    // and load the lang details using getLangById()
    this.langId = this.route.snapshot.paramMap.get('id');
    if (this.langId !== null) {
      this.mode = 'update';
      const data = await this.crudService.get(this.langId, 'options');
      if (data) {
        this.form.patchValue({
          title: data.title,
          title_en: data.title_en,
          title_it: data.title_it,
          title_de: data.title_de,
          thumbnail: data.thumbnail,
          drink: data.drink,
          active: data.active,
          type: data.type
        });
      }
    } else {
      this.defaults = {} as any;

    }
  }

  save() {
    if (this.langId) {
      this.crudService.update(this.langId, this.form.value, 'options', 'option');
    } else {
      this.form.value.type = this.sanitizeString(this.form.value.title);
      this.crudService.create(this.form.value, 'options', 'option');
    }
  }

  sanitizeString(str: string): string {
    // Eliminar acentos y diacríticos
    str = str.normalize('NFD').replace(/[\u0300-\u036f]/g, '');

    // Eliminar caracteres especiales que no son alfanuméricos ni espacios
    str = str.replace(/[^a-zA-Z0-9\s]/g, '');

    // Reemplazar espacios por guiones bajos
    str = str.replace(/\s+/g, '_');

    // Convertir a minúsculas
    str = str.toLowerCase();

    return str;
  }

  // Custom Validator
  lengthValidator(control: AbstractControl) {
    const value = control.value;
    const regex = /^[a-zA-Z]{2,5}$/;
    return regex.test(value) ? null : { length: true };
  }

  cancel() {
    this.router.navigate(['/option']); // Navigating to /language on cancel
  }
}
<vex-secondary-toolbar current="Opciones">
  <vex-breadcrumbs [crumbs]="['Opciones', 'Gestión de Opciones de carta']" class="flex-auto"></vex-breadcrumbs>

  <button class="ml-2" color="primary" mat-icon-button type="button">
    <mat-icon svgIcon="mat:more_vert"></mat-icon>
  </button>
</vex-secondary-toolbar>

<div [@stagger]="true" class="p-gutter container">

  <div class="flex flex-col sm:flex-row gap-4">
    <div @fadeInUp class="card flex-auto">
      <div class="px-6 py-4 border-b flex items-center">
        <h2 class="title m-0">Gestor de opciones de carta</h2>
      </div>

      <div class="px-6 py-4 flex flex-col" [formGroup]="form">
        <mat-form-field>
          <mat-label>Nombre</mat-label>
          <input matInput formControlName="title" required>
          <mat-hint class="mat-small" *ngIf="form.get('title').invalid && form.get('title').touched">
            Este campo es obligatorio
          </mat-hint>
        </mat-form-field>
        <mat-form-field>
          <mat-label>Nombre Inglés</mat-label>
          <input matInput formControlName="title_en" required>
          <mat-hint class="mat-small" *ngIf="form.get('title_en').invalid && form.get('title_en').touched">
            Este campo es obligatorio
          </mat-hint>
        </mat-form-field>
        <mat-form-field>
          <mat-label>Nombre Italiano</mat-label>
          <input matInput formControlName="title_it" required>
          <mat-hint class="mat-small" *ngIf="form.get('title_it').invalid && form.get('title_it').touched">
            Este campo es obligatorio
          </mat-hint>
        </mat-form-field>
        <mat-form-field>
          <mat-label>Nombre Alemán</mat-label>
          <input matInput formControlName="title_de" required>
          <mat-hint class="mat-small" *ngIf="form.get('title_de').invalid && form.get('title_de').touched">
            Este campo es obligatorio
          </mat-hint>
        </mat-form-field>
        <div>
          <mat-label>¿Con imagen de miniatura? <b>(Si activas esta opción, los items podrán incluir una miniatura)</b></mat-label><br>
          <mat-slide-toggle style="flex: 1;" formControlName="thumbnail"></mat-slide-toggle>

        </div>
        <div>
          <mat-label>¿Tiene precio por copa y botella? <b>(Si activas esta opción, los items podrán incluir precios diferentes por copa y botella)</b></mat-label><br>
          <mat-slide-toggle style="flex: 1;" formControlName="drink"></mat-slide-toggle>

        </div>
        <div>
          <mat-label>Activo <b>(Si activas esta opción, se visualizará en el menu de la web)</b></mat-label><br>
          <mat-slide-toggle style="flex: 1;" formControlName="active"></mat-slide-toggle>

        </div>

        <div class="flex items-center justify-end gap-2">
          <button mat-button type="button" (click)="cancel()">Cancelar</button> <!-- Added (click) event here -->
          <button color="primary" mat-raised-button type="button" (click)="save()">Guardar</button>
        </div>
      </div>
    </div>
  </div>
</div>
