import { fadeInUp400ms } from 'src/@vex/animations/fade-in-up.animation';
import { stagger20ms } from 'src/@vex/animations/stagger.animation';
import { getFirestore, Firestore, addDoc, collection, updateDoc, doc, deleteDoc, getDocs, getDoc, writeBatch  } from '@angular/fire/firestore';
import { getStorage, ref, deleteObject, uploadString, getDownloadURL, uploadBytesResumable } from "firebase/storage";
import { MatSnackBar } from '@angular/material/snack-bar';
import { ActivatedRoute, Router } from '@angular/router';


@Component({
  selector: 'vex-menu-create-update',
  templateUrl: './menu-create-update.component.html',
  styleUrls: ['./menu-create-update.component.scss'],
  animations: [fadeInUp400ms, stagger20ms]
})
export class MenuCreateUpdateComponent implements OnInit {

  static id = null;
  docId: any = null;;
  type: any = null;

  form: UntypedFormGroup;
  mode: 'create' | 'update' = 'create';
  defaults: any;
  options: any = [];

  langs: any[] = [];
  menuTitle: string = null;
  isActive: boolean = false;
  loading: boolean = true;

  tabs: any = [];
  selectedTabIndex = 0;

  constructor(private fb: UntypedFormBuilder, private firestore: Firestore, private matSnackBar: MatSnackBar, private router: Router, private route: ActivatedRoute) {
  }

  ngOnInit() {
    // Obtén el ID de la ruta.
    this.docId = this.route.snapshot.paramMap.get('id');
    this.getOptions();
    //this.getLanguages();
    if (this.docId !== null) {
      this.mode = 'update';
      this.getById();
    } else {
      this.defaults = {} as any;

    }
  }


  async getLanguages() {
    const db = getFirestore();
    const languagesCollection = collection(db, 'languages');
    const languagesSnapshot = await getDocs(languagesCollection);
    this.langs = languagesSnapshot.docs.map(doc => doc.data().code);
    this.loading = false;
  }


  async getOptions() {
    const db = getFirestore();
    const optionsCollection = collection(db, 'options');
    const optionsSnapshot = await getDocs(optionsCollection);
    this.options = optionsSnapshot.docs.map(doc => doc.data());
    this.getLanguages();

  }

  handleSelection(value: string) {
    this.type = value;
    console.log(`Tipo seleccionado: ${this.type}`);

    const exist = this.tabs.length > 0 ? this.tabs.menu.filter(t => t?.type === this.type.type).length > 0 : false;

    if (!exist) {
      this.addTab();
    } else {
      this.matSnackBar.open("Ya hay un apartado de este tipo de carta. Rellena los subapartados", 'OK', {duration: 3000})
    }
  }


  isCreateMode() {
    return this.mode === 'create';
  }

  isUpdateMode() {
    return this.mode === 'update';
  }

  submit() {
    if (this.mode === 'update') {
      this.updateMenu();
    } else {
      this.createMenu();
    }
  }

  async createMenu() {
    try {
      const menu = {menu: this.tabs.menu, active: this.isActive, title: this.menuTitle};
      if (this.isActive) {
        const menuRef = collection(this.firestore, 'menus');
        const menuSnap = await getDocs(menuRef);
        const batch = writeBatch(this.firestore);

        menuSnap.forEach((doc) => {
          batch.update(doc.ref, { active: false });
        });

        await batch.commit();
      }
      const docRef = await addDoc(collection(this.firestore, 'menus'), menu);
      this.matSnackBar.open("El menu se ha creado correctamente", 'CERRAR', {duration: 3000});
      this.router.navigate(['menu']);
    } catch (e) {
      console.error('Error adding document: ', e);
    }
  }

  async updateMenu(): Promise<void> {
    try {

      if (this.isActive) {
        const menuRef = collection(this.firestore, 'menus');
        const menuSnap = await getDocs(menuRef);
        const batch = writeBatch(this.firestore);

        menuSnap.forEach((doc) => {
          batch.update(doc.ref, { active: false });
        });

        await batch.commit();
      }

      const menuRef = doc(collection(this.firestore, 'menus'), this.docId);
      await updateDoc(menuRef, this.tabs);
      this.matSnackBar.open("El menu se ha modificado correctamente", 'CERRAR', {duration: 3000});
      this.router.navigate(['menu']);} catch (error) {
      console.error('Error updating menu:', error);
    }
  }

  async getById() {


    if (this.docId) {
      const db = getFirestore();
      const docRef = doc(db, "menus", this.docId);

      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        // El documento existe.
        this.tabs = docSnap.data();
        this.type = this.options.filter(op => op.type === this.tabs.menu[0].type)[0];
        this.menuTitle = this.tabs.title;
        this.loading = false;
      } else {
        // El documento no existe.
        throw new Error("No document with given id");
      }
    }
  }

  setTitle(event, tab) {
    tab.title = event.target.value;
  }

  addTab() {

    if (this.tabs.length > 0) {
      this.tabs.menu.push({ title: this.type.title, subTabs:[], type: this.type.type });

    } else {
      this.tabs.menu = [];
      this.tabs.menu.push({ title: this.type.title, subTabs:[], type: this.type.type });

    }
    this.selectedTabIndex = this.tabs.menu.length - 1;  // Cambia al índice de la nueva pestaña
    this.addSubTab();
  }

  getTabsByType() {
    const tabs = this.tabs.menu && this.tabs.menu.length > 0 ? this.tabs.menu.filter(t => t.type === this.type.type) : [];
    return tabs.length > 0 ? tabs : [];
  }

  addSubTab() {

    this.tabs.menu[this.selectedTabIndex].subTabs.push({ langs: {}, title: ''});

    this.langs.forEach(element => {

      this.tabs.menu[this.selectedTabIndex].subTabs.forEach(subT => {
        subT.langs[element] = [];/*
          subT.langs[element].push({
            image: null,
            title: '',
            description: '',
            bottlePrice: '',
            cupPrice: '',
            imageRead: '',
            origin: ''
          });*/
      });
    });
  }

  removeTab(index: number) {
    this.tabs.splice(index, 1);
    if (this.selectedTabIndex >= index) {
      this.selectedTabIndex = Math.max(this.selectedTabIndex - 1, 0);
    }
  }

  removeSubTab(index: number, tab) {
    tab.subTabs.splice(index, 1);
  }

  addToTab(tab: any, subTab: any) {
    this.langs.forEach(element => {

      if (tab.type === 'vinos') {
        subTab.langs[element].push({
          image: null,
          title: '',
          description: '',
          bottlePrice: '',
          cupPrice: '',
          imageRead: '',
          origin: ''
        })
      } else {

        subTab.langs[element].push({
          title: '',
          price: '',
          description: ''
        });
      }
    });
  }

  removeToTab(lang: string, tab: any, index: number) {

    this.deleteImage(lang, index, tab);
    for (const lang of this.langs) {
      tab.langs[lang].splice(index, 1);
    }
  }

  onSelect(event, lang: string, i: number, tab: any) {
    if (event.addedFiles.length > 0) {
      const file = event.addedFiles[0];
      if (!(file instanceof File)) {
        console.error('El archivo seleccionado no es un objeto de tipo File:', file);
        return;
      }
      const storage = getStorage();

      // Creamos un identificador único para el nombre de cada imagen.
      const uniqueId = Math.random().toString(36).substring(2);

      // Creamos una referencia al lugar donde queremos subir el archivo: 'images/{uniqueId}'
      const storageRef = ref(storage, `images/${uniqueId}`);

      // Subimos el archivo a Firebase Storage.
      const uploadTask = uploadBytesResumable(storageRef, file);

      // Escuchamos los cambios de estado de la subida del archivo y capturamos los datos del archivo subido.
      uploadTask.on('state_changed',
        (snapshot) => {},
        (error) => {
          console.error('Error al subir el archivo:', error);
        },
        () => {
          getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
            console.log('File available at', downloadURL);
            tab.langs[lang][i].imageRead = downloadURL;

            for (const lang of this.langs) {
              tab.langs[lang][i].imageRead = downloadURL;
            }
          });
        }
      );
    }
  }

  async deleteImage(lang: string, i: number, tab: any) {
    // Create a reference to the file to delete
    const storage = getStorage();
    const storageRef = ref(storage, tab.langs[lang][i].imageRead);

    // Delete the file
    try {
      await deleteObject(storageRef);
      console.log('File deleted');
    } catch(error) {
      console.error('Error deleting file', error);
    }
  }
}

<vex-secondary-toolbar current="Menu">
  <vex-breadcrumbs [crumbs]="['Cartas', 'Gestión de Cartas']" class="flex-auto"></vex-breadcrumbs>

  <button class="ml-2" color="primary" mat-icon-button type="button">
    <mat-icon svgIcon="mat:more_vert"></mat-icon>
  </button>
</vex-secondary-toolbar>
<div [@stagger]="true" class="p-gutter container" style="max-width: none" *ngIf="!loading">

  <!-- Contenedor Flexbox para alinear los elementos horizontalmente -->
  <div style="display: flex; gap: 20px; align-items: center; justify-content: start;">
    <!-- Input para el título -->
    <mat-form-field appearance="fill" style="flex: 1;">
      <mat-label>Título para la carta</mat-label>
      <input matInput placeholder="Ingrese el título de la carta" [(ngModel)]="menuTitle">
    </mat-form-field>

    <!-- Toggle para el atributo 'active' -->
    <mat-slide-toggle style="flex: 1;" [(ngModel)]="isActive">¿Activar?</mat-slide-toggle>

  </div>

  <div style="display: flex; align-items: center; justify-content: space-between;">
    <mat-form-field appearance="fill" style="width: 50%">
      <mat-label>Que tipo de carta quieres crear</mat-label>
      <mat-select (selectionChange)="handleSelection($event.value)" [(ngModel)]="type">
        <span *ngFor="let item of options">
          <mat-option *ngIf="item.active" [value]="item">{{item.title}}</mat-option>

        </span>
      </mat-select>
    </mat-form-field>
    <button mat-raised-button color="primary" style="float:right" *ngIf="type !== null" (click)="submit()">Guardar</button>
  </div>


<!--<button *ngIf="type!==null" mat-raised-button color="primary" (click)="addTab()" style="float: left;">Añadir</button>-->
<button *ngIf="mode === 'update' ? tabs.menu.length > 0 : tabs.length > 0" mat-raised-button color="primary" (click)="addSubTab()" style="float: left;">Añadir sub apartado</button>

  <mat-tab-group style="width:100%">

    <mat-tab *ngFor="let tab of getTabsByType(); let idx = index">
      <ng-template mat-tab-label>
        <!--<input [(ngModel)]="tab.title" placeholder="Título de la pestaña" [value]="type.title" readonly="readonly">
        <button mat-button (click)="removeTab(idx)">X</button>
        -->
        <p>{{ type.title }}</p>

      </ng-template>
      <!-- Aquí va el contenido de tu pestaña, como los elementos del menú -->

      <mat-tab-group *ngIf="type.drink && type.active">
        <mat-tab *ngFor="let subTab of tab.subTabs; let subI = index">
          <ng-template mat-tab-label>
            <input [(ngModel)]="subTab.title" placeholder="Título de la pestaña" (input)="setTitle($event, subTab)">
            <button mat-button (click)="removeSubTab(subI, tab)">X</button>
          </ng-template>
          <mat-tab-group>
            <mat-tab *ngFor="let lang of langs; let i = index" label="{{lang.toUpperCase()}}" >

              <button class="rounded-full" color="primary" mat-raised-button type="button" (click)="addToTab(tab, subTab)" style="float:right">
                <mat-icon class="ltr:-ml-1 rtl:-mr-1 ltr:mr-2 rtl:ml-2"
                          svgIcon="mat:add"></mat-icon>
                <span>Añadir</span>
              </button>

              <div *ngFor="let item of subTab.langs[lang]; let i = index" style="float:left; margin: 20px 10px 0 0; width:28%;">
                <button mat-icon-button color="warn" (click)="removeToTab(lang, subTab, i)">
                  <mat-icon svgIcon="mat:delete"></mat-icon>
                </button>

                <div class="dropzone">
                  <ngx-dropzone [multiple]="false" (change)="onSelect($event, lang, i, subTab)">
                    <img *ngIf="subTab.langs[lang][i]?.imageRead !== null" [src]="subTab.langs[lang][i]?.imageRead" alt="Imagen">
                  </ngx-dropzone>
                </div>
                <mat-form-field class="example-full-width">
                  <mat-label>Titulo</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].title" placeholder="Nombre">
                </mat-form-field>

                <mat-form-field class="example-full-width">
                  <mat-label>Precio</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].price" placeholder="Precio">
                </mat-form-field>

                <mat-form-field class="example-full-width">
                  <mat-label>Descripcion</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].description" placeholder="Descripción">
                </mat-form-field>

                <mat-form-field class="example-full-width">
                  <mat-label>Precio botella</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].bottlePrice" placeholder="Precio botella">
                </mat-form-field>

                <mat-form-field class="example-full-width">
                  <mat-label>Precio por copa</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].cupPrice" placeholder="Precio copa">
                </mat-form-field>

                <mat-form-field class="example-full-width">
                  <mat-label>Origen</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].origin" placeholder="Origen">
                </mat-form-field>
              </div>
            </mat-tab>
          </mat-tab-group>
        </mat-tab>
      </mat-tab-group>

      <mat-tab-group *ngIf="!type.drink && type.active">
        <mat-tab *ngFor="let subTab of tab.subTabs; let subI = index">
          <ng-template mat-tab-label>
            <input [(ngModel)]="subTab.title" placeholder="Título de la pestaña" (input)="setTitle($event, subTab)">
            <button mat-button (click)="removeSubTab(subI, tab)">X</button>
          </ng-template>
          <mat-tab-group>
            <mat-tab *ngFor="let lang of langs; let i = index" label="{{lang.toUpperCase()}}" >

              <button class="rounded-full" color="primary" mat-raised-button type="button" (click)="addToTab(tab, subTab)" style="float:right">
                <mat-icon class="ltr:-ml-1 rtl:-mr-1 ltr:mr-2 rtl:ml-2"
                          svgIcon="mat:add"></mat-icon>
                <span>Añadir</span>
              </button>

              <div *ngFor="let item of subTab.langs[lang]; let i = index" style="float:left; margin: 0 10px 0 0">
                <button mat-icon-button color="warn" (click)="removeToTab(lang, subTab, i)">
                  <mat-icon svgIcon="mat:delete"></mat-icon>
                </button>
                <mat-form-field class="example-full-width">
                  <mat-label>Titulo</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].title" placeholder="Nombre">
                </mat-form-field>

                <mat-form-field class="example-full-width">
                  <mat-label>Precio</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].price" placeholder="Precio">
                </mat-form-field>

                <mat-form-field class="example-full-width">
                  <mat-label>Descripcion</mat-label>
                  <input matInput [(ngModel)]="subTab.langs[lang][i].description" placeholder="Descripción">
                </mat-form-field>
              </div>
            </mat-tab>
          </mat-tab-group>
        </mat-tab>
      </mat-tab-group>
    </mat-tab>
  </mat-tab-group>

</div>
