import { Component } from '@angular/core';
import { TableColumn } from 'src/@vex/interfaces/table-column.interface';
import { defaultChartOptions } from 'src/@vex/utils/default-chart-options';
import { tableSalesData } from 'src/app/static-data/table-sales-data';

@Component({
  selector: 'vex-dashboard-analytics',
  templateUrl: './dashboard-analytics.component.html',
  styleUrls: ['./dashboard-analytics.component.scss']
})
export class DashboardAnalyticsComponent {
  tableColumns: TableColumn<any>[] = [
    {
      label: '',
      property: 'status',
      type: 'badge'
    },
    {
      label: 'PRODUCT',
      property: 'name',
      type: 'text'
    },
    {
      label: '$ PRICE',
      property: 'price',
      type: 'text',
      cssClasses: ['font-medium']
    },
    {
      label: 'DATE',
      property: 'timestamp',
      type: 'text',
      cssClasses: ['text-secondary']
    }
  ];
  tableData = tableSalesData;

  series: ApexAxisChartSeries = [{
    name: 'Subscribers',
    data: [28, 40, 36, 0, 52, 38, 60, 55, 67, 33, 89, 44]
  }];

  userSessionsSeries: ApexAxisChartSeries = [
    {
      name: 'Users',
      data: [10, 50, 26, 50, 38, 60, 50, 25, 61, 80, 40, 60]
    },
    {
      name: 'Sessions',
      data: [5, 21, 42, 70, 41, 20, 35, 50, 10, 15, 30, 50]
    },
  ];

  salesSeries: ApexAxisChartSeries = [{
    name: 'Sales',
    data: [28, 40, 36, 0, 52, 38, 60, 55, 99, 54, 38, 87]
  }];

  pageViewsSeries: ApexAxisChartSeries = [{
    name: 'Page Views',
    data: [405, 800, 200, 600, 105, 788, 600, 204]
  }];

  uniqueUsersSeries: ApexAxisChartSeries = [{
    name: 'Unique Users',
    data: [356, 806, 600, 754, 432, 854, 555, 1004]
  }];

  uniqueUsersOptions = defaultChartOptions({
    chart: {
      type: 'area',
      height: 100
    },
    colors: ['#ff9800']
  });
}
<vex-page-layout>

  <vex-secondary-toolbar current="Dashboard">
    <vex-breadcrumbs [crumbs]="['Dashboards', 'Analytics']" class="flex-auto"></vex-breadcrumbs>

    <button class="ml-2" color="primary" mat-icon-button type="button">
      <mat-icon svgIcon="mat:more_vert"></mat-icon>
    </button>
  </vex-secondary-toolbar>
  <vex-page-layout-content class="-mt-6 fullwidth">
    <div class="container p-gutter grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
      <vex-widget-assistant class="sm:col-span-2"></vex-widget-assistant>

      <vex-widget-quick-line-chart icon="mat:pageview"
                                   [series]="pageViewsSeries"
                                   iconClass="text-primary bg-primary/10"
                                   label="Page Views"
                                   value="92.4k"></vex-widget-quick-line-chart>

      <vex-widget-quick-line-chart icon="mat:group"
                                   [options]="uniqueUsersOptions"
                                   [series]="uniqueUsersSeries"
                                   iconClass="text-orange bg-orange/10"
                                   label="Unique Users"
                                   value="67.2k"></vex-widget-quick-line-chart>

      <vex-widget-large-chart [series]="userSessionsSeries"
                              class="sm:col-span-2"></vex-widget-large-chart>

      <vex-widget-table [columns]="tableColumns"
                        [data]="tableData"
                        class="sm:col-span-2"></vex-widget-table>

      <vex-widget-quick-value-center [change]="124.4"
                                     icon="mat:group"
                                     iconClass="text-primary bg-primary/10"
                                     label="Users"
                                     value="67.4k"></vex-widget-quick-value-center>
      <vex-widget-quick-value-center [change]="-23.7"
                                     icon="mat:pageview"
                                     iconClass="text-orange bg-orange/10"
                                     helpText="Additional information"
                                     label="Sessions"
                                     value="96.2k"></vex-widget-quick-value-center>
      <vex-widget-quick-value-center [change]="23.2"
                                     icon="mat:cloud_off"
                                     iconClass="text-deep-orange bg-deep-orange/10"
                                     helpText="Explain something"
                                     label="Bounce Rate"
                                     value="6.92%"></vex-widget-quick-value-center>
      <vex-widget-quick-value-center [change]="34.5"
                                     icon="mat:timer"
                                     iconClass="text-teal bg-teal/10"
                                     label="Session Duration"
                                     value="16m 48s"></vex-widget-quick-value-center>

      <vex-widget-large-goal-chart [series]="salesSeries"
                                   class="sm:col-span-full"
                                   total="4,374"></vex-widget-large-goal-chart>

    </div>
  </vex-page-layout-content>
</vex-page-layout>
