    this.langs.forEach(element => {
      this.champagnes.langs[element].push({
        image: null,
        title: '',
        description: '',
        bottlePrice: '',
        cupPrice: '',
        imageRead: '',
        origin: ''
      });
    });
  }

  addWhiteWine() {
    this.langs.forEach(element => {
      this.whiteWine.langs[element].push({
        image: null,
        title: '',
        description: '',
        bottlePrice: '',
        cupPrice: '',
        imageRead: '',
        origin: ''
      });
    });
