  submit() {
    if (this.mode === 'update') {
      this.updateMenu();
    } else {
      this.createMenu();
    }
  }

  async createMenu() {
    const menu = {
      title: this.menuTitle,
      snacks: this.snacks.langs,
      starters: this.starters.langs,
      fishes: this.fishes.langs,
      meats: this.meats.langs,
      deserves: this.deserves.langs,
      coffes: this.coffes.langs,
      drinks: this.drinks.langs,
      champagnes: this.champagnes.langs,
      whiteWine: this.whiteWine.langs,
      roseWine: this.roseWine.langs,
      redWine: this.redWine.langs,
      sweetWine: this.sweetWine.langs,
      active: this.isActive
    };
    try {
      if (this.isActive) {
        const menuRef = collection(this.firestore, 'menus');
        const menuSnap = await getDocs(menuRef);
        const batch = writeBatch(this.firestore);

        menuSnap.forEach((doc) => {
          batch.update(doc.ref, { active: false });
        });

        await batch.commit();
      }
      const docRef = await addDoc(collection(this.firestore, 'menus'), menu);
      this.matSnackBar.open("El menu se ha creado correctamente", 'CERRAR', {duration: 3000});
      this.router.navigate(['menu']);
    } catch (e) {
      console.error('Error adding document: ', e);
    }
  }

  async updateMenu(): Promise<void> {
    try {
      const menu = {
        title: this.menuTitle,
        snacks: this.snacks.langs,
        starters: this.starters.langs,
        fishes: this.fishes.langs,
        meats: this.meats.langs,
        deserves: this.deserves.langs,
        coffes: this.coffes.langs,
        drinks: this.drinks.langs,
        champagnes: this.champagnes.langs,
        whiteWine: this.whiteWine.langs,
        roseWine: this.roseWine.langs,
        redWine: this.redWine.langs,
        sweetWine: this.sweetWine.langs,
        active: this.isActive
      };

      if (this.isActive) {
        const menuRef = collection(this.firestore, 'menus');
        const menuSnap = await getDocs(menuRef);
        const batch = writeBatch(this.firestore);

        menuSnap.forEach((doc) => {
          batch.update(doc.ref, { active: false });
        });

        await batch.commit();
      }

      const menuRef = doc(collection(this.firestore, 'menus'), this.docId);
      await updateDoc(menuRef, menu);
      this.matSnackBar.open("El menu se ha modificado correctamente", 'CERRAR', {duration: 3000});
      this.router.navigate(['menu']);    } catch (error) {
      console.error('Error updating menu:', error);
    }
  }

  async getById() {


    if (this.docId) {
      const db = getFirestore();
      const docRef = doc(db, "menus", this.docId);

      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        // El documento existe.
        this.snacks.langs = docSnap.data().snacks;
        this.starters.langs = docSnap.data().starters;
        this.fishes.langs = docSnap.data().fishes;
        this.meats.langs = docSnap.data().meats;
        this.deserves.langs = docSnap.data().deserves;
        this.coffes.langs = docSnap.data().coffes;
        this.drinks.langs = docSnap.data().drinks;
        this.champagnes.langs = docSnap.data().champagnes;
        this.whiteWine.langs = docSnap.data().whiteWine;
        this.roseWine.langs = docSnap.data().roseWine;
        this.redWine.langs = docSnap.data().redWine;
        this.sweetWine.langs = docSnap.data().sweetWine;
        this.menuTitle = docSnap.data().title;
        this.isActive = docSnap.data().active;
        console.log({ id: docSnap.id, ...docSnap.data() });
        this.loading = false;
      } else {
        // El documento no existe.
        throw new Error("No document with given id");
      }
    }
  }

  setTitle(event, tab) {
    tab.title = event.value;
  }

  addTab() {

    this.tabs.push({ title: '', subTabs:[] });
    this.selectedTabIndex = this.tabs.length - 1;  // Cambia al índice de la nueva pestaña
    this.addSubTab();
  }

  addSubTab() {

    this.tabs[this.selectedTabIndex].subTabs.push({ langs: {} });

    this.langs.forEach(element => {

      this.tabs[this.selectedTabIndex].subTabs.forEach(subT => {
        subT.langs[element] = [];
          subT.langs[element].push({
            image: null,
            title: '',
            description: '',
            bottlePrice: '',
            cupPrice: '',
            imageRead: '',
            origin: ''
          });
      });
    });
  }

  removeTab(index: number) {
    this.tabs.splice(index, 1);
    if (this.selectedTabIndex >= index) {
      this.selectedTabIndex = Math.max(this.selectedTabIndex - 1, 0);
    }
  }

  removeSubTab(index: number, tab) {
    tab.subTabs.splice(index, 1);
  }

  addToTab(tab: any) {
    this.langs.forEach(element => {
      tab.langs[element].push({
        title: '',
        price: '',
        description: ''
      });
    });
  }

  removeToTab(tab: any, index: number) {

    for (const lang of this.langs) {
      tab.langs[lang].splice(index, 1);
    }
  }

  onSelect(event, lang: string, i: number, tab: any) {
    if (event.addedFiles.length > 0) {
      const file = event.addedFiles[0];
      if (!(file instanceof File)) {
        console.error('El archivo seleccionado no es un objeto de tipo File:', file);
        return;
      }
      const storage = getStorage();

      // Creamos un identificador único para el nombre de cada imagen.
      const uniqueId = Math.random().toString(36).substring(2);

      // Creamos una referencia al lugar donde queremos subir el archivo: 'images/{uniqueId}'
      const storageRef = ref(storage, `images/${uniqueId}`);

      // Subimos el archivo a Firebase Storage.
      const uploadTask = uploadBytesResumable(storageRef, file);

      // Escuchamos los cambios de estado de la subida del archivo y capturamos los datos del archivo subido.
      uploadTask.on('state_changed',
        (snapshot) => {},
        (error) => {
          console.error('Error al subir el archivo:', error);
        },
        () => {
          getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
            console.log('File available at', downloadURL);
            tab.langs[lang][i].imageRead = downloadURL;

            for (const lang of this.langs) {
              tab.langs[lang][i].imageRead = downloadURL;
            }
          });
        }
      );
    }
  }

  async deleteImage(lang: string, i: number, tab: any) {
    // Create a reference to the file to delete
    const storage = getStorage();
    const storageRef = ref(storage, tab.langs[lang][i].imageRead);

    // Delete the file
    try {
      await deleteObject(storageRef);
      console.log('File deleted');
    } catch(error) {
      console.error('Error deleting file', error);
    }
  }
}

