import { Injectable } from '@angular/core';
import { Auth, signInWithEmailAndPassword, signOut, onAuthStateChanged, getAuth } from '@angular/fire/auth';
import { Router } from '@angular/router';
import { User } from '@firebase/auth';
import { createUserWithEmailAndPassword } from '@angular/fire/auth';
import { UserService } from './userService';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  user: User | null = null;

  constructor(private auth: Auth,private router: Router, private userService: UserService) {
    const user = JSON.parse(localStorage.getItem('sotanoUser'));
    if (user) {
      this.user = user;
    }
    onAuthStateChanged(auth, (user) => {
      if (user) {
        this.user = user;
        localStorage.setItem('sotanoUser', JSON.stringify(user));
      } else {
        this.user = null;
        localStorage.removeItem('sotanoUser');
      }
    });
  }

  async login(email: string, password: string) {
    try {
      const auth = getAuth();
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      if (userCredential.user) {
        localStorage.setItem('sotanoUser', JSON.stringify(userCredential.user));
        this.router.navigate(['/menu']);
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  }

  async register(email: string, password: string): Promise<void> {
    try {
      const auth = getAuth();
      await createUserWithEmailAndPassword(auth, email, password);
      this.router.navigate(['/menu']);
    } catch (error) {
      console.error('Error during registration:', error);
    }
  }

  async logout() {
    await signOut(this.auth);
    this.user = null;
    localStorage.removeItem('sotanoUser');
  }

  isLoggedIn() {
    return this.user !== null;
  }
}
