import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators, AbstractControl } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { fadeInUp400ms } from 'src/@vex/animations/fade-in-up.animation';
import { stagger20ms } from 'src/@vex/animations/stagger.animation';
import { CrudService } from 'src/service/crudService';

@Component({
  selector: 'vex-options-create-update',
  templateUrl: './options-create-update.component.html',
  styleUrls: ['./options-create-update.component.scss'],
  animations:[stagger20ms, fadeInUp400ms]
})
export class OptionsCreateUpdateComponent {
  form: FormGroup;
  langId: string | null = null;
  mode: string | null = null;
  defaults: any;

  constructor(
    private fb: FormBuilder,
    private crudService: CrudService,
    private route: ActivatedRoute,
    private router: Router
  ) {
    this.form = this.fb.group({
      title: ['', Validators.required]
    });
  }

  async ngOnInit() {
    // if you are editing an existing lang, set langId
    // and load the lang details using getLangById()
    this.langId = this.route.snapshot.paramMap.get('id');
    if (this.langId !== null) {
      this.mode = 'update';
      const langData = await this.crudService.getLangById(this.langId, 'options');
      if (langData) {
        this.form.patchValue({
          name: langData.name
        });
      }
    } else {
      this.defaults = {} as any;

    }
  }

  save() {
    if (this.langId) {
      this.crudService.updateLang(this.langId, this.form.value, 'options');
    } else {
      this.crudService.createLang(this.form.value, 'options');
    }
  }

  // Custom Validator
  lengthValidator(control: AbstractControl) {
    const value = control.value;
    const regex = /^[a-zA-Z]{2,5}$/;
    return regex.test(value) ? null : { length: true };
  }

  cancel() {
    this.router.navigate(['/language']); // Navigating to /language on cancel
  }
}
<vex-secondary-toolbar current="Opciones">
  <vex-breadcrumbs [crumbs]="['Opciones', 'Gestión de Opciones de carta']" class="flex-auto"></vex-breadcrumbs>

  <button class="ml-2" color="primary" mat-icon-button type="button">
    <mat-icon svgIcon="mat:more_vert"></mat-icon>
  </button>
</vex-secondary-toolbar>

<div [@stagger]="true" class="p-gutter container">

  <div class="flex flex-col sm:flex-row gap-4">
    <div @fadeInUp class="card flex-auto">
      <div class="px-6 py-4 border-b flex items-center">
        <h2 class="title m-0">Gestor de opciones de carta</h2>
      </div>

      <div class="px-6 py-4 flex flex-col" [formGroup]="form">
        <mat-form-field>
          <mat-label>Nombre</mat-label>
          <input matInput formControlName="title" required>
          <mat-hint class="mat-small" *ngIf="form.get('title').invalid && form.get('title').touched">
            Este campo es obligatorio
          </mat-hint>
        </mat-form-field>

        <div class="flex items-center justify-end gap-2">
          <button mat-button type="button" (click)="cancel()">Cancel</button> <!-- Added (click) event here -->
          <button color="primary" mat-raised-button type="button" (click)="save()">Guardar</button>
        </div>
      </div>
    </div>
  </div>
</div>
